package extern.nifti.alignstacks.align3tp;import java.lang.RuntimeException;/** * This class provides a runtime exception which represents a user attempt * to kill an overly long calculation. */public class UserEscapeException extends RuntimeException {	public UserEscapeException() {		super();		return;	}	public UserEscapeException(String s) {		super(s);		return;	}}	// end UserEscapeException